import React, { useState } from "react";

const playerList = [
  "Pratham", "Arjan", "Madhav", "Sid", "Jattan", "Arjun", "Saksham", "Japnam", "Kevin", "Surya", "Shiv", "Satvik", "Yash", "Kshitij", "Cristian", "Jaival"
];
const rankLabels = [
  "1st Overall", "2nd Overall", "3rd Overall", "4th Overall", "5th Overall",
  "6th Overall", "7th Overall", "8th Overall", "9th Overall", "10th Overall"
];

export default function App() {
  const numRanks = 10;
  const [selections, setSelections] = useState(Array(numRanks).fill(""));
  const [submitted, setSubmitted] = useState(false);

  React.useEffect(() => {
    // Inject Google Fonts (Poppins)
    const link = document.createElement('link');
    link.href = 'https://fonts.googleapis.com/css2?family=Poppins:wght@500;700&display=swap';
    link.rel = 'stylesheet';
    document.head.appendChild(link);
    return () => { document.head.removeChild(link); };
  }, []);

  const handleSelect = (idx, value) => {
    setSelections((prev) => {
      const next = [...prev];
      next[idx] = value;
      return next;
    });
  };

  const handleReset = () => {
    setSelections(Array(numRanks).fill(""));
    setSubmitted(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    if (selections.some((s) => !s)) return;
    let msg = "Your Rankings:\n";
    for (let i = 0; i < numRanks; i++) {
      msg += `${rankLabels[i]}: ${selections[i]}\n`;
    }
    window.alert(msg);
  };

  // For each dropdown, show all unselected players + its own selection
  const getDropdownOptions = (idx) => {
    const used = selections.filter((sel, i) => sel && i !== idx);
    const available = playerList.filter((name) => !used.includes(name));
    // If this dropdown has a selection, add it back
    if (selections[idx] && !available.includes(selections[idx])) {
      available.push(selections[idx]);
    }
    return available.sort((a, b) => a.localeCompare(b));
  };

  return (
    <div className="pr-bg">
      <div className="pr-container">
        <h1 className="pr-title">Powerranks</h1>
        <p className="pr-subtitle">Rank your <span>Top 10</span> players</p>
        <form className="pr-form" onSubmit={handleSubmit} autoComplete="off">
          {rankLabels.map((label, idx) => (
            <div className="pr-rank-row" key={idx}>
              <label className="pr-rank-label" htmlFor={`rank${idx}`}>{label}</label>
              <div className="pr-select-wrap">
                <select
                  id={`rank${idx}`}
                  value={selections[idx]}
                  onChange={(e) => handleSelect(idx, e.target.value)}
                  className={submitted && !selections[idx] ? "pr-error" : ""}
                >
                  <option value="">-- Select Player --</option>
                  {getDropdownOptions(idx).map((name) => (
                    <option key={name} value={name}>{name}</option>
                  ))}
                </select>
                <span className="pr-chevron" aria-hidden>▼</span>
              </div>
            </div>
          ))}
          <div className="pr-actions">
            <button type="button" className="pr-reset-btn" onClick={handleReset}>
              Reset
            </button>
            <button
              type="submit"
              className="pr-submit-btn"
              disabled={selections.some((s) => !s)}
            >
              Submit Rankings
            </button>
          </div>
        </form>
      </div>
      <style>{`
        body, #root {
          min-height: 100vh;
        }
        .pr-bg {
          min-height: 100vh;
          width: 100vw;
          background: linear-gradient(135deg, #e0e7ff 0%, #f7f8fa 60%, #fff 100%);
          display: flex;
          align-items: center;
          justify-content: center;
          font-family: 'Poppins', 'Segoe UI', Arial, sans-serif;
        }
        .pr-container {
          background: rgba(255,255,255,0.98);
          border-radius: 2rem;
          box-shadow: 0 8px 32px rgba(80,80,180,0.13), 0 1.5px 8px rgba(0,0,0,0.04);
          padding: 2.5rem 2rem 2rem 2rem;
          max-width: 420px;
          width: 100%;
          margin: 2rem 0;
          display: flex;
          flex-direction: column;
          align-items: center;
        }
        .pr-title {
          font-size: 2.3rem;
          font-weight: 700;
          letter-spacing: -1px;
          color: #2a2a5a;
          margin-bottom: 0.2em;
        }
        .pr-subtitle {
          font-size: 1.1rem;
          color: #5a5a8a;
          margin-bottom: 2rem;
        }
        .pr-subtitle span {
          color: #007bff;
          font-weight: 600;
        }
        .pr-form {
          width: 100%;
        }
        .pr-rank-row {
          display: flex;
          align-items: center;
          margin-bottom: 1.2rem;
        }
        .pr-rank-label {
          flex: 0 0 120px;
          font-weight: 500;
          color: #3a3a6a;
          font-size: 1.04rem;
        }
        .pr-select-wrap {
          position: relative;
          flex: 1;
        }
        select {
          width: 100%;
          padding: 0.6rem 2.2rem 0.6rem 0.9rem;
          border-radius: 0.8rem;
          border: 1.5px solid #d1d5db;
          font-size: 1.04rem;
          background: #f4f7ff;
          color: #2a2a5a;
          font-family: inherit;
          appearance: none;
          outline: none;
          transition: border 0.2s, box-shadow 0.2s;
          box-shadow: 0 1.5px 6px rgba(0,0,0,0.03);
        }
        select:focus {
          border: 1.5px solid #007bff;
          box-shadow: 0 0 0 2px #b3d1ff44;
        }
        select.pr-error {
          border: 1.5px solid #e74c3c;
          background: #fff6f6;
        }
        .pr-chevron {
          position: absolute;
          right: 1rem;
          top: 50%;
          transform: translateY(-50%);
          pointer-events: none;
          color: #7a7a9a;
          font-size: 1.1em;
        }
        .pr-actions {
          display: flex;
          justify-content: space-between;
          margin-top: 2.2rem;
          gap: 1.1rem;
        }
        .pr-submit-btn {
          flex: 1;
          padding: 0.85rem 0;
          border: none;
          border-radius: 1.2rem;
          font-size: 1.08rem;
          font-weight: 700;
          background: linear-gradient(90deg, #007bff 60%, #5f7cff 100%);
          color: #fff;
          box-shadow: 0 2px 8px #007bff22;
          cursor: pointer;
          transition: background 0.18s, box-shadow 0.18s, transform 0.13s;
        }
        .pr-submit-btn:disabled {
          background: linear-gradient(90deg, #b3d1ff 60%, #dbeafe 100%);
          color: #fff;
          cursor: not-allowed;
          box-shadow: none;
        }
        .pr-submit-btn:not(:disabled):hover {
          background: linear-gradient(90deg, #0056b3 60%, #3b5cff 100%);
          transform: translateY(-2px) scale(1.03);
          box-shadow: 0 4px 16px #007bff33;
        }
        .pr-reset-btn {
          flex: 1;
          padding: 0.85rem 0;
          border: none;
          border-radius: 1.2rem;
          font-size: 1.08rem;
          font-weight: 700;
          background: linear-gradient(90deg, #f3f3f3 60%, #e0e7ff 100%);
          color: #5a5a8a;
          box-shadow: 0 2px 8px #b3b3b322;
          cursor: pointer;
          transition: background 0.18s, box-shadow 0.18s, transform 0.13s;
        }
        .pr-reset-btn:hover {
          background: linear-gradient(90deg, #e0e7ff 60%, #f3f3f3 100%);
          color: #2a2a5a;
          transform: translateY(-2px) scale(1.03);
          box-shadow: 0 4px 16px #b3b3b344;
        }
        @media (max-width: 600px) {
          .pr-container {
            padding: 1.1rem 0.3rem 1.2rem 0.3rem;
            max-width: 99vw;
            border-radius: 1.1rem;
          }
          .pr-title {
            font-size: 1.3rem;
          }
          .pr-rank-label {
            flex: 0 0 90px;
            font-size: 0.97rem;
          }
        }
      `}</style>
    </div>
  );
}
