import React, { useState } from "react";

const playerList = [
  "Pratham", "Arjan", "Madhav", "Sid", "Jattan", "Arjun", "Saksham", "Japnam", "Kevin", "Surya", "Shiv", "Satvik", "Yash", "Kshitij", "Cristian", "Jaival"
];
const rankLabels = [
  "1st Overall", "2nd Overall", "3rd Overall", "4th Overall", "5th Overall",
  "6th Overall", "7th Overall", "8th Overall", "9th Overall", "10th Overall"
];

export default function App() {
  const numRanks = 10;
  const [selections, setSelections] = useState(Array(numRanks).fill(""));
  const [submitted, setSubmitted] = useState(false);

  const handleSelect = (idx, value) => {
    setSelections((prev) => {
      const next = [...prev];
      next[idx] = value;
      return next;
    });
  };

  const handleReset = () => {
    setSelections(Array(numRanks).fill(""));
    setSubmitted(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    if (selections.some((s) => !s)) return;
    let msg = "Your Rankings:\n";
    for (let i = 0; i < numRanks; i++) {
      msg += `${rankLabels[i]}: ${selections[i]}\n`;
    }
    window.alert(msg);
  };

  // For each dropdown, show all unselected players + its own selection
  const getDropdownOptions = (idx) => {
    const used = selections.filter((sel, i) => sel && i !== idx);
    const available = playerList.filter((name) => !used.includes(name));
    // If this dropdown has a selection, add it back
    if (selections[idx] && !available.includes(selections[idx])) {
      available.push(selections[idx]);
    }
    return available.sort((a, b) => a.localeCompare(b));
  };

  return (
    <div className="container">
      <h1>Rank Your Top 10 Players</h1>
      <form className="ranking-form" onSubmit={handleSubmit} autoComplete="off">
        {rankLabels.map((label, idx) => (
          <div className="rank-row" key={idx}>
            <label className="rank-label" htmlFor={`rank${idx}`}>{label}</label>
            <select
              id={`rank${idx}`}
              value={selections[idx]}
              onChange={(e) => handleSelect(idx, e.target.value)}
              className={submitted && !selections[idx] ? "error" : ""}
            >
              <option value="">-- Select Player --</option>
              {getDropdownOptions(idx).map((name) => (
                <option key={name} value={name}>{name}</option>
              ))}
            </select>
          </div>
        ))}
        <div className="actions">
          <button type="button" className="reset-btn" onClick={handleReset}>
            Reset
          </button>
          <button
            type="submit"
            className="submit-btn"
            disabled={selections.some((s) => !s)}
          >
            Submit Rankings
          </button>
        </div>
      </form>
      <style>{`
        .container {
          background: #fff;
          margin: 2rem auto;
          padding: 2rem 1.5rem;
          border-radius: 12px;
          box-shadow: 0 2px 12px rgba(0,0,0,0.07);
          max-width: 400px;
          width: 100%;
        }
        h1 {
          text-align: center;
          margin-bottom: 1.5rem;
          font-size: 1.7rem;
          color: #222;
        }
        .rank-row {
          display: flex;
          align-items: center;
          margin-bottom: 1rem;
        }
        .rank-label {
          flex: 0 0 110px;
          font-weight: 500;
          color: #444;
          font-size: 1rem;
        }
        select {
          flex: 1;
          padding: 0.5rem;
          border-radius: 6px;
          border: 1px solid #ccc;
          font-size: 1rem;
          background: #fafbfc;
          transition: border 0.2s;
        }
        select:focus {
          border: 1.5px solid #007bff;
          outline: none;
        }
        select.error {
          border: 1.5px solid #e74c3c;
          background: #fff6f6;
        }
        .actions {
          display: flex;
          justify-content: space-between;
          margin-top: 2rem;
          gap: 1rem;
        }
        button {
          flex: 1;
          padding: 0.7rem 0;
          border: none;
          border-radius: 6px;
          font-size: 1rem;
          font-weight: 600;
          cursor: pointer;
          transition: background 0.2s, color 0.2s;
        }
        .submit-btn {
          background: #007bff;
          color: #fff;
        }
        .submit-btn:disabled {
          background: #b3d1ff;
          color: #fff;
          cursor: not-allowed;
        }
        .reset-btn {
          background: #f3f3f3;
          color: #333;
        }
        @media (max-width: 500px) {
          .container {
            padding: 1rem 0.5rem;
            max-width: 98vw;
          }
          .rank-label {
            flex: 0 0 90px;
            font-size: 0.97rem;
          }
          h1 {
            font-size: 1.2rem;
          }
        }
      `}</style>
    </div>
  );
}
